				<!-- Message -->
				<div class="form-control">
					<label for="message" class="label">
						<span class="label-text">Message*</span>
					</label>
					<textarea
						id="message"
						v-model="formData.message"
						required
						aria-required="true"
						class="textarea textarea-bordered w-full"
					></textarea>
					<span v-if="errors.message" class="text-error text-sm">{{
						errors.message
					}}</span>
				</div>

				<!-- Hidden IP Address -->
				<input type="hidden" v-model="formData.ip" />

				<!-- Send -->
				<button
					type="submit"
					class="btn btn-outline w-full text-lg"
					role="button"
				>
					Submit
				</button>
			</form>
		</div>
		<div
			v-if="toast.message"
			:class="`alert alert-${toast.type} mt-4 text-white`"
		>
			{{ toast.message }}
		</div>
	</div>
</template>

<script setup>
import { reactive, watch, ref, onMounted } from 'vue'

const fetchIpAddress = async () => {
	try {
		const response = await fetch('https://api.ipify.org?format=json')
		const data = await response.json()
		return data.ip
	} catch (error) {
		console.error('Error fetching IP address:', error)
		return ''
	}
}

// Utility function to sanitize inputs
const sanitizeInput = (input) => {
	const tempElement = document.createElement('div')
	tempElement.innerText = input
	return tempElement.innerHTML
}

const formData = reactive({
	name: '',
	email: '',
	phone: '',
	message: '',
	ip: '', // New field for IP address
})

onMounted(async () => {
	formData.ip = await fetchIpAddress()
})

const errors = reactive({})
const toast = reactive({
	message: '',
	type: '',
})

const formStarted = ref(false)

watch(
	() => [formData.name, formData.email, formData.phone, formData.message],
	(newValues) => {
		if (!formStarted.value && newValues.some((value) => value !== '')) {
			if (typeof gtag === 'function') {
				gtag('event', 'form started', {
					event_category: 'Contact Form',
					event_label: 'Form Started',
				})
			}
			formStarted.value = true
		}
	},
	{ immediate: false }
)

const validateForm = () => {
	errors.name = formData.name ? '' : 'Name is required.'
	errors.email = formData.email ? '' : 'Email is required.'
	errors.email = validEmail(formData.email) ? '' : 'Valid email is required.'
	errors.message = formData.message ? '' : 'Message is required.'

	return !Object.values(errors).some((error) => error)
}

const validEmail = (email) => {
	const re =
		/^(([^<>()\[\]\.,;:\s@"]+(\.[^<>()\[\]\.,;:\s@"]+)*)|(".+"))@(([^<>()[\]\.,;:\s@"]+\.)+[^<>()[\]\.,;:\s@"]{2,})$/i
	return re.test(email)
}

const sanitizeFormData = () => {
	formData.name = sanitizeInput(formData.name)
	formData.email = sanitizeInput(formData.email)
	formData.phone = sanitizeInput(formData.phone)
	formData.message = sanitizeInput(formData.message)
}

const submitForm = () => {
	sanitizeFormData()

	if (validateForm()) {
		if (typeof gtag === 'function') {
			gtag('event', 'submit', {
				event_category: 'Contact Form',
				event_label: 'Form Submission',
			})
		}

		fetch('https://ottawawebmasters.ca/contact.php', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
			},
			body: JSON.stringify(formData),
		})
			.then((response) => response.json())
			.then((data) => {
				if (data.status === 'success') {
					showToast('Message sent successfully!', 'success')
					resetForm()
				} else {
					showToast('An error occurred. Please try again.', 'error')
				}
			})
			.catch(() => {
				showToast('An error occurred. Please try again.', 'error')
			})
	}
}

const showToast = (message, type) => {
	toast.message = message
	toast.type = type
	setTimeout(() => {
		toast.message = ''
		toast.type = ''
	}, 3000)
}

const resetForm = () => {
	formData.name = ''
	formData.email = ''
	formData.phone = ''
	formData.message = ''
	formData.ip = '' // Reset IP field
	Object.keys(errors).forEach((key) => (errors[key] = ''))
	formStarted.value = false
}
</script>

